<?php if ( ! defined('BASEPATH')) exit('No direct script access allowed');//Clase login_model para acceso de usuariosclass login_model extends CI_Model {//Constructor de la clase	function __construct()     {          parent::__construct();     }/*  * Funcion CreaMenu para crear el menu segu el rol*/     public function CreaMenu($idUser){     	$sql ="select U.ID, U.NOMBRE, U.APELLIDOS, AU.Usuario, AU.Proteccion, AU.Estatus, M.Id as IdMenu, M.Linea, ";     	$sql = $sql." M.Descripcion, M.URL From usuarios as U inner join accesosusuarios as AU on U.ID=AU.Usuario ";		$sql = $sql." inner join menu as M on AU.Proteccion = M.Id WHERE AU.Usuario='".$idUser."' AND AU.Estatus=1 ORDER BY M.Id ASC";		$query=$this->db->query($sql);		return $query->result();     }/*  * Funcion LoginBD Para verificar al usuario */      function LoginBD($username)     {          $this->db->where('EMAIL', $username);          //$this->db->where('PASSWORD', $password);          return $this->db->get('usuarios')->row();     }}