<?php if ( ! defined('BASEPATH')) exit('No direct script access allowed');//Clase usuarios_model Para realizar ABC de el catalogo  class usuarios_model extends CI_Model {//Constructor de la clase	function __construct()     {          parent::__construct();     }/*  * Funcion Listar usuarios Para consultar todos los usuarios registrados en la base de datos */     public function ListarUsuarios(){		$sql="SELECT  * from usuarios ORDER BY  NOMBRE ASC ";		$query=$this->db->query($sql);		return $query->result();	}/*  * Funcion Buscar usuarios Para buscar los usuarios registrados en la base de datos */	public function BuscarUsuario($id){		$sql="SELECT  * from usuarios where ID='".$id."' limit 1 ";		$query=$this->db->query($sql);		return $query->result();	}/*  * Funcion Save usuarios Para almacenar al usuario en la base de datos */	public function SaveUsuarios($array){		$id = 0;		$this->db->trans_start();     	$this->db->insert('usuarios', $array);     	$id = $this->db->insert_id();     	$this->db->trans_complete();     	return $id;	}/*  * Funcion Update usuario Para actualizar la inf del usuario registrado en la base de datos */	public function UpdateUsers($array,$id){		$this->db->trans_start();		$this->db->where('ID', $id);		$this->db->update('usuarios', $array); 		$this->db->trans_complete();	}/*  * Funcion ExisteEmail Para verificar o consultar email del usuario registrado en la base de datos */	public function ExisteEmail($email){		$this->db->where("EMAIL",$email);        $check_exists = $this->db->get("usuarios");        if($check_exists->num_rows() == 0){            return false;        }else{            return true;        }	}/*  * Funcion Eliminar usuario Para eliminar al usuario registrado en la base de datos */	public function EliminarUsuario($id){		$this->db->where('ID',$id);		return $this->db->delete('usuarios');	}/*  * Funcion AsignaPermisosAdmin Para asignar privilegios al usuario registrado en la base de datos */	public function AsignaPermisosAdmin($array){		$this->db->trans_start();     	$this->db->insert('accesosusuarios', $array);     	$this->db->trans_complete();	}        }